import React, { useState } from 'react';
import axios from 'axios';
import './GithubRepoChecker.css';

const GithubRepoChecker = () => {
  const [githubUrl, setGithubUrl] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setResult(null);
    setError(null);

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/check-repo/', { githubUrl });
      setResult(response.data);
    } catch (err) {
      setError('There was an error checking the repository. Please try again.');
    }
  };

  return (
    <div className="github-repo-checker">
      <h1>Check GitHub Repository</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter GitHub repo URL"
          value={githubUrl}
          onChange={(e) => setGithubUrl(e.target.value)}
          className="github-input"
        />
        <br />
        <button type="submit">Check Repository</button>
      </form>
      {result && (
        <div className="result">
          <h2>Analysis Results</h2>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
      {error && (
        <div className="error" style={{ color: 'red' }}>
          <p>{error}</p>
        </div>
      )}
    </div>
  );
};

export default GithubRepoChecker;
